mapper.update:
update debate
set debate_title = #{ debate_title }, debate_content = #{ debate_content }
where debate_num = #{ debate_num }

update debate
set debate_comment_count = (select debate_comment_count from debate where debate_num = #{ debate_num }) + 1
where debate_num = #{ debate_num }

update debate
set debate_comment_count = (select debate_comment_count from debate where debate_num = #{ debate_num }) - 1
where debate_num = #{ debate_num }
